mapper.select:
select ename,eid,tel,pwd,sname,dname from (select rownum rn ,a.* from (select ename,eid,tel,pwd,sname,dname from Employer e,department d,sysrole s where e.did=d.did and e.sid=s.sid) a where #{pageNo}*#{pageSize} >= rownum ) where rn> (#{pageNo}-1) * #{pageSize}

	and tel=#{tel}


	and ename = #{ename}


	and dname=#{dept}


	and sname=#{sname}

select count(eid) from Employer e,department d,sysrole s where e.did=d.did and e.sid=s.sid

	and tel=#{tel}


	and ename like #{ename}


	and dname=#{dept}


	and sname=#{sname}
