mapper.select:
select rid,to_char(rdate,'yyyy-mm-dd') rdate,price,imid,iname,ename,proof,remark,eid,cashier,deptmanager,topmanager,financer,rstatus from (select rownum rn ,a.* from (select r.*,ename,iname from reiminfo r , employer e,fitems f where r.eid=e.eid and r.imid=f.imid) a where #{pageNo}*#{pageSize} >= rownum ) where rn> (#{pageNo}-1) * #{pageSize}

	and to_char(rdate,'yyyy-mm-dd')='#{date}'


	and imid=#{imid}


	and rstatus=#{status}


	and price >= #{startprice}


	and  #{endprice} >= price

